CREATE OR REPLACE VIEW dpcr_fases_proc AS 
 SELECT sf.id_fase,
    spf.nombre,
    sf.id_pcd,
    sf.id_resp,
    sf.estado,
    scp.nombre AS nombre_proc,
    spp.nversion,
    scp.id_padre,
    se.fapertura
   FROM spac_fases sf,
    spac_p_fases spf,
    spac_ct_procedimientos scp,
    spac_p_procedimientos spp,
    spac_expedientes se
  WHERE sf.id_fase = spf.id AND sf.id_pcd = scp.id AND scp.id = spp.id AND spp.tipo = 1 AND sf.estado = 1 AND sf.numexp = se.numexp;



